VALUES
	(1,1,'2016-11-14 15:47:56','2016-11-14 14:47:56','Bienvenue dans WordPress. Ceci est votre premier article. Modifiez-le ou supprimez-le, puis lancez-vous&nbsp;!','Bonjour tout le monde&nbsp;!','','publish','open','open','','bonjour-tout-le-monde','','','2016-11-14 15:47:56','2016-11-14 14:47:56','',0,'http://rocket.js/?p=1',0,'post','',1),
